import { useState } from "react";

//backend api display on the page 

const App =() => {
  const [users,setUsers]=useState([]);

  const fetchUserData = () => {
    fetch('http://localhost:7000/api/users', {
      method:"GET",
    })
    .then((response) => {
      return response.json();
    })
    .then((data)=> setUsers(data.users))
    .catch((err) => console.log(err));

   // alert ("fetch user from api");request api feach the user
  };

  return (
  <div className="container">
    {/* <pre>{JSON.stringify(users,null,4)}</pre> */}


     {users && users.map((user) => <div>{user.name}</div>)}


    <button onClick={fetchUserData} className="btn btn-primary">
      Fetch user data
      </button>
  </div>
);

};

export default App;


